INSERT INTO `palavraPastor` (`id`, `titulo`, `texto`, `padre`, `foto`) VALUES
(1, 'Canonização de Carlo Acutis: do coração do Brasil, um milagre que ganhou o mundo', 'O Papa Leão XIV canonizou o adolescente italiano Carlo Acutis em celebração realizada no Vaticano neste domingo (7). O jovem, que faleceu aos 15 anos devido a uma leucemia, tornou-se o primeiro santo da geração millennial na história da Igreja Católica.', '', 'papa.webp');

